import { useRef, useState } from "react";
import "./App.css";
import ReactPlayer from "react-player";

function App() {
  const player = [
    { name: "Video1", link: "https://www.youtube.com/watch?v=VU23OPQ1Pmc" },
    { name: "Video2", link: "https://www.youtube.com/watch?v=epAFDEJImrU" },
    { name: "Video3", link: "https://www.youtube.com/watch?v=TZuQjv5Z6h8" },
    { name: "Video4", link:  "https://www.youtube.com/watch?v=3r_ZNfAl7yY"},

  ];
  const [current, setCurrent] = useState(player[0].link);
  const videoRef = useRef(null);
  useEffect(() => {

      const timer = setTimeout(() => {

        if (videoRef.current) {
          videoRef.current.load(); // Reload the video element with the new URL
        }
      }, 2000); // Delay of 2 seconds

      return () => clearTimeout(timer); // Cleanup the timer

  }, [current]);
  return (
    <div className="App">
      {player.map((item, index) => {
        return (
          <button key={index} onClick={() => setCurrent(item.link)}>
            {item.name}
          </button>
        );
      })}
      <ReactPlayer
        className="react-player"
        url={current + "?v=" + Date.now()}
        width="750px"
        height="750px"
        style={{ border: "1px solid black", padding: "10px" }}
        controls={true}
        pip={true}
      />




<video ref={videoRef} controls width="600">
        <source src={current + "?v=" + Date.now()} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
    </div>
  );
}

export default App;
